const express = require('express');
const bodyParser = require('body-parser');
const mongoose = require('mongoose');

const app = express();
const PORT = 5000;

app.use((req, res, next) => {
    res.header('Access-Control-Allow-Origin', '*'); // You can set specific origins instead of '*'
    res.header('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
    res.header('Access-Control-Allow-Headers', 'Content-Type, Authorization');
    next();
  });

// Connect to MongoDB
mongoose.connect('mongodb://localhost:27017/pdfExtension', { useNewUrlParser: true, useUnifiedTopology: true });

app.use(bodyParser.json());

// Define a MongoDB model
const Download = mongoose.model('Download', {
  url: String,
});

// Define a route to store URLs
app.post('/api/store', async (req, res) => {
  try {
    const { url } = req.body;
    const download = new Download({ url });
    await download.save();
    res.status(201).json({ message: 'URL stored successfully' });
    console.log(download)
  } catch (error) {
    console.error(error);
    res.status(500).json({ message: 'Error storing URL' });
  }
});

app.listen(PORT, () => {
  console.log(`Server is running on port ${PORT}`);
});